INSERT INTO `cafe_cards` (`id`, `title`, `content`, `desc`, `link`, `name_link`, `address`, `coordinate_l`, `coordinate_r`, `preview_image`, `logo`, `phone`, `user_id`, `type_id`, `district_id`, `republic_id`, `city_id`, `created_at`, `updated_at`, `deleted_at`, `user_email`) VALUES
(2, 'Кафе «Баку»', 'Рады приветствовать Вас в нашем кафе. Вы можете провести незабываемый вечер пробуя нашу еду. Кроме того, мы всегда рады видеть Вас на бизнес-ланч, у нас прекрасное обеденное меню.', 'Кафе «Баку»', 'https://tourmeddriver.com/catalog/cafe/1', 'Кафе «Баку»', 'Азербайджан, Баку, проспект Нефтяников 17', '53.910346570631795', '27.54740399999998', 'cafe_images_card/Bf620TfVhX7NAblSAGdbFh48myWnPlVF9NTLumoT.jpg', 'cafe_images_card/xn9uJ6rR7fXkOMbNeb2VAB54rzDgv7v4xZUJJ16Y.jpg', 994123478834, 4, 3, 8, 14, 21, '2023-02-04 13:21:18', '2024-09-15 11:38:18', NULL, 'cafe@mail.ru'),
(3, 'Чайный дом «Чайное облако»', 'У нас есть всё необходимое для чайных церемоний. Вы можете уединиться от городской суеты, или собраться компанией, окунувшись в атмосферу чайной церемонии, и конечно же приобрести чай с собой.\r\nВ нашем чайном доме вы можете отдохнуть душой и телом.', 'Чайный дом «Чайное облако»  - это место, где вы можете приобрести «правильный» чай', 'https://vk.com/chaynoyeoblako', 'https://vk.com/chaynoyeoblako', 'г. Геленджик, ул.Крымская 22, корпус 16', '44.549438', '38.056741', 'cafe_images_card/gIa7whGoPz0jtRTHCkTemAqjY4WHOewwEPEFAQQD.jpg', 'cafe_images_card/Ucwq8nrV3yW68GJQzZrlxjYlv3DmFWKWGWiiLJ8y.jpg', 79384454450, 27, 3, 7, 13, 19, '2023-06-20 18:34:05', '2024-09-15 11:40:17', NULL, 'saharovaev@mail.ru'),
(4, 'Море Барабульки', 'Адреса:\r\nул.Приморский бульвар, д7 \r\nЛермонтовский бульвар, напр. кафе «Парус» \r\nс.Дивноморское, центральная наб, вблизи схода №3 \r\nЛермонтовский бульвар д9 к 2 \r\nх.Бетта, Приморский бульвар д22\r\nсело Архипо-Осиповка, Курортная ул', 'Ресторан Море Барабульки -  у нас можно заказать еду', 'https://yandex.by/profile/20689685439', 'https://yandex.by/profile/20689685439', 'Россия, г.Геленджик, ул.Приморский бульвар, д7 (МАГНОЛИЯ)', '44.567171', '38.073854', 'cafe_images_card/XLhBDSF94w1IMO2Eqhv7yxIdnEzYIWR1Bml389Vk.jpg', 'cafe_images_card/OEicVdTlvxvqZBaC6wedLKtkkjEyu5strq2OwQLU.jpg', 79280380791, 35, 3, 7, 13, 19, '2023-07-08 07:57:51', '2024-09-15 11:41:46', NULL, 'barabulka.fish@bk.ru'),
(5, 'Кафе \"Здравствуйте\"', 'В Только в нашем кафе три вида обслуживания: линия раздачи, где вы можете увидеть все блюда своими глазами, банкетный зал и летняя площадка, где Вас обслужат официанты. Также рядом есть две больше комфортные автостоянки', 'Кафе \"Здравствуйте\" - отличное кафе в Геленджике', 'http://kafe-zdravstvuyte.ru', 'http://kafe-zdravstvuyte.ru', 'Россия, Геленджик,  ул. Кирова, 117', '44.568642', '38.089601', 'cafe_images_card/7Cy9j9o4ebL4GDgA8q5rVfav97mWk3laZsdsIbhe.jpg', 'cafe_images_card/IHry6Q7eY53Knm6Xyz1NBPKWwEYSpBL9Y7hBWTB2.jpg', 79288508080, 37, 3, 7, 13, 19, '2023-07-09 10:41:55', '2024-09-15 11:43:01', NULL, 'zdravstvuite30@yandex.ru'),
(6, 'Ресторан Джумейра', 'Кухня:\r\nЯпонская, Европейская, Кавказская, Грузинская', 'Джумейра - отличный ресторан в Дербенте', 'https://vk.com/public211530946', 'https://vk.com/public211530946', 'Россия, Дербент, Вокзальная, 27/1', '42.057495', '48.305234', 'cafe_images_card/ApeJjccYw7SBrD3CzvHGSTCjpOi8I7JAQx5S2Pkm.jpg', 'cafe_images_card/uzVsHdxMwuytM23AnQL3VlOMrLNeLHIDa27kcV6Z.jpg', 79284600505, 38, 1, 7, 11, 22, '2023-07-10 07:20:19', '2024-09-15 11:46:09', NULL, 'jumeirah_istoria@mail.ru'),
(7, 'Ресторан Тихие Берега', '«Тихие берега» — гастрономическая доминанта уютной семейной усадьбы в Геленджике. С летней террасы открывается вид на каскад винограда элитных сортов. В ресторане ценят культурные и гастрономические традиции южного региона.', 'Тихие берега - уютная семейная усадьба в Геленджике', 'https://тихие-берега.рф', 'https://тихие-берега.рф', 'Россия, Геленджик, село Адербиевка ул. Советская, 56', '44.600522', '38.125354', 'cafe_images_card/AlOu56fUhKPUThOiD7kSuzXfZ5wfyqNxaVFkf0hH.jpg', 'cafe_images_card/S7WOkQqrkb6X8Hl1Dpe7QBkkW9uZvSVId1yszcTf.png', 79897773897, 39, 3, 7, 13, 19, '2023-07-14 15:01:38', '2024-09-15 11:47:37', NULL, 'tihie_berega@mail.ru'),
(8, 'Торговый дом \"ДНК Природы\"', 'Функциональное питание-содержит максимальное количество природных веществ, витаминов и минералов, насыщает организм белками, жирами, углеводами, аминокислотами.', 'Функциональное питание – это специальная система сбалансированных продуктов, способная удовлетворить потребности организма в биоактивных полезных веществах значительно выше обычной еды.', 'https://dnk-prirodi.ru/', 'https://dnk-prirodi.ru/', 'Москва, Санкт-Петербург', '55.77113', '55.77113', 'cafe_images_card/OIOKhCL9BgmfrItLdeIOolpd2deGWMITfDZs2cbQ.png', 'cafe_images_card/f0qd0oco02zJCapD1JnpjE098ioiFvNX3uAK1jv0.jpg', 89119411015, 40, 3, 1, 7, 12, '2023-08-04 14:07:38', '2023-09-07 10:27:30', NULL, 'amdyachkov@bk.ru'),
(9, 'ssss', 'asd', 'asd', 'http://127.0.0.1:8000/', 'Pheasants', 'Беларусь, Минск, проспект Победителей 17', '53.910346570631795', '27.54740399999998', 'cafe_images_card/NEUx3z5H2Uz4uwFtEpl58YSJCtZmp7qkNGyVZrdw.jpg', 'cafe_images_card/05r3H7yugGMn9M93kKmjS5CKUTtArBJ7EwApD2QL.jpg', 375173478834, 41, 3, 1, 1, 1, '2023-08-11 08:58:34', '2023-08-11 08:59:09', '2023-08-11 08:59:09', 'medica1l@mail.ru');

