INSERT INTO `consulting_cats` (`id`, `title`, `created_at`, `updated_at`, `content`) VALUES
(1, 'Аудит компании', '2023-08-18 15:21:01', '2024-01-24 07:15:45', 'Инициативный аудит организация проводит исходя из потребности самой организации.\r\nКомплекс мероприятий, включающих сбор, оценку, анализ, заключение с целью объективного видения и планирования. Необходимость в аудиторской услуге может возникнуть при системных изменениях компании, а также для более точного стратегирования и проектирования и планирования  дальнейшей деятельности.'),
(2, 'Стратег. сессии', '2023-08-18 15:21:09', '2024-01-24 08:49:08', '<p>- Определение и/или обновление стратегии\r\n</p><p>- Установление приоритетов\r\n</p><p>- Развитие бизнеса\r\n</p><p>- Улучшение операционной эффективности</p><p>\r\n- Решение проблем и противостояние кризису</p><p>\r\n- Повышение вовлеченности сотрудников</p><p>\r\n- Адаптация к изменениям</p>'),
(3, 'Концепция и бизнес модель', '2023-08-18 15:21:15', '2024-01-24 07:47:25', 'Бизнес модель – это концептуальный инструмент для функционирования бизнес системы, отражающий логику бизнеса. От того, на сколько удачен выбор и реализация бизнес модели, во многом зависит судьба компании. Системная суть бизнеса и его бизнес модели работают для достижения цели организации.'),
(4, 'Образовательные программы', '2023-08-18 15:21:24', '2024-01-24 09:32:50', '<p>Для ТОП менеджмента\r\n</p><p>Для линейного персонала\r\n</p><p>Для кадрового потенциала (HR директора)</p><p>\r\nКомандообразование\r\nМаркетинг</p><p>Продажи.</p>'),
(5, 'Программа всесезонного заполнения', '2023-08-24 12:45:28', '2024-01-24 07:49:22', 'Концептуальные, маркетинговые и структурные решения в организации, приводящие к регулярному клиентопотоку. План и дорожная карта реализации программы всесезонного наполнения клиентами.'),
(6, 'Сопровождение бизнеса', '2023-08-24 12:46:52', '2024-01-24 07:49:57', 'Экологичное прохождение сквозь кризисы организации, закономерно сопровождающие рост и развитие системы. Профессиональное сопровождение позволяет эффективно своевременно, точно и мягко справляться с неминуемой кризисной нагрузкой на разных этапах развития организации.'),
(7, 'Юридическое сопровождение', '2023-08-24 12:47:15', '2024-01-24 07:50:57', 'Сопровождение в правовом поле, профилактирующее и решающее необходимые юридические аспекты организации.'),
(8, 'Цифровая трансформация', '2023-08-24 12:47:54', '2024-01-24 07:51:32', 'Автоматизация и оптимизация системы за счет внедрения цифровых технологий'),
(9, 'Профессиональные компетенции', '2023-08-24 12:48:45', '2024-01-24 07:52:11', 'Отраслевые программы как для управленцев и топ менеджеров, так и для линейного персонала.'),
(10, 'Дизайн мышления', '2023-08-24 12:49:02', '2024-01-24 07:52:49', 'Работа с учредителями и руководителями бизнеса, позволяющая диагностировать ограничивающие убеждения, устранять их и менять видение и масштаб деятельности с целью расширения и масштабирования бизнеса'),
(11, 'Международная интеграция', '2023-09-12 09:45:52', '2024-01-24 07:53:25', 'Формирование условий и возможностей для вывода бизнеса в международное пространство.');

