INSERT INTO `hostel_cards` (`id`, `title`, `content`, `desc`, `link`, `name_link`, `address`, `coordinate_l`, `coordinate_r`, `preview_image`, `logo`, `phone`, `user_id`, `type_id`, `district_id`, `republic_id`, `city_id`, `created_at`, `updated_at`, `coordinate`, `deleted_at`, `user_email`) VALUES
(1, 'Pheasants', 'Добро пожаловать в наш отель. Здесь для Вас самые лучшие номера на любой вкус. Мы заботимся о наших клиентах и всегда поддерживаем номера в самом лучшем виде.', 'Мы заботимся о наших клиентах.', 'https://turmeddriver.d0053501.atservers.net/', 'Pheasants', 'Грузия, Тбилиси, Улица Абано 24', '41.688123500330946', '44.81122249999989', 'hostel_images_card/4xpsDC6PwXRoFZcZszLNGaxKYeW31Tp4NcA6RLev.jpg', 'hostel_images_card/9YlPkktwbUJjFFZsPw4OhYuADH0PS1TIuMfrFCV2.jpg', 955322147903, 5, 5, 6, 8, 13, '2023-02-22 08:32:43', '2024-10-15 08:31:58', NULL, NULL, 'hostel@mail.ru'),
(2, 'Гостевой дом Ракушка Блага', 'Бескрайнее море и песчаные пляжи. Современные номера. Озелененная территория. Детская площадка. Бассейн. Мангальная зона . Кондиционер, ЖК телевизор и бесплатный wi-fi. Аниматор для детей.', 'Гостевой дом Ракушка Блага', 'rakushka-blaga.com', 'rakushka-blaga.com', 'Анапа, Краснодарский край ст-ца Благовещенская, ул. Таманская 142А', '45.056524', '37.122394', 'hostel_images_card/Ybc6VOWGktCw37RAKRzcsLopzPCKLWBBZaUBQ8aA.webp', 'hostel_images_card/RCPtigF3Ph1npu4NTqMg5yYeC8qWqiJvXBzKgiGV.webp', 79881313205, 11, 1, 1, 1, 6, '2023-03-20 14:46:28', '2023-05-29 19:12:23', NULL, NULL, 'rakushka023@yandex.ru'),
(3, 'Golden Kids', 'Все номера Golden Kids имеют выход на балкон, а в некоторых - на террасу, где можно насладиться шикарным видом на горы или город. Есть кабельное телевидение, бесплатный Wi-Fi, детская игровая и всё, что нужно для яркого семейного отдыха', 'Golden Kids - отель для приятного семейного отдыха в Геленджике', 'https://goldenkids-hotel.ru/', 'https://goldenkids-hotel.ru/', 'Россия, г. Геленджик, ул. Шевченко, 32', '44.556394', '38.079019', 'hostel_images_card/pnHqpAVN0TC5bOeps4sRRSUzvg0IJUxRO88QCy2E.jpg', 'hostel_images_card/4b5YO6TMpFZuwtamX4zz6suMDNNRCBMUG0jucEng.jpg', 79385053000, 18, 4, 7, 13, 19, '2023-05-30 18:22:34', '2024-10-15 07:16:55', NULL, NULL, 'admin@mail.ru'),
(4, 'Апарт-отель Brio', 'Мы находимся в тихом и спокойном районе, недалеко от моря, что обеспечит вам комфортное проживание, спокойный и уютный отдых и вы можете наслаждаться солнцем и морем в любое время. Отель подходит для отдыха семей или небольших групп друзей.', 'Комфортный отель всего в 200 метрах от моря.', 'http://brio-hotel.ru', 'http://brio-hotel.ru', 'Геленджик, улица Грибоедова 20/2', '44.553144', '38.073225', 'hostel_images_card/gXrjkepD7iblqdCjtBBGmdKV6CUIQMG83DV6VEOT.jpg', 'hostel_images_card/NfiLUcptGVe8Ig97sUvhnL11xhoY9ZIz8TXLD0kf.jpg', 79384906303, 24, 4, 1, 6, 7, '2023-06-07 17:35:22', '2023-06-07 17:35:22', NULL, NULL, 'admin@mail.ru'),
(5, 'Апарт-отель OmNashDom', 'Отель расположен в красивом районе Толстый мыс, всего в 900 метрах от моря, что делает его отличным выбором для отдыха у моря. В нашем отеле каждый момент наполнен спокойствием, покоем и внутренней гармонией.', 'Мы приложим все усилия для комфортного и безопасного отдыха', 'http://omnashdom.ru', 'http://omnashdom.ru', 'Геленджик, улица Розы Люксембург 26', '44.566195', '38.085873', 'hostel_images_card/ekVzs5Y8O5jogySTQbolIuHkK0XDrEiZ91K5fqPZ.jpg', 'hostel_images_card/Nui4w3JLY0Yqt2KP3gRmcb6voMo61fpAHcOTiCHD.jpg', 79384906303, 24, 4, 1, 6, 7, '2023-06-07 17:54:17', '2023-06-07 17:54:17', NULL, NULL, 'admin@mail.ru'),
(6, 'Бутик-отель Атлантида', 'Новый современный отель в центре Геленджика. Расстояние до моря и набережной 300 м. В номерах есть все для самого комфортного отдыха', 'Новый современный отель в центре Геленджика', 'Hotel-atlantida.ru', 'Hotel-atlantida.ru', 'Геленджик ул. Советская, 21', '44.557358', '38.079279', 'hostel_images_card/wAjHsCdtdUjROrXil3WeShrAgdF4jxHJXjGdOfHE.jpg', 'hostel_images_card/znRYryPrrkKcY1CY5OWcZ3pRw035ibihiEeIueSD.jpg', 79628551900, 25, 4, 7, 13, 19, '2023-06-08 17:30:06', '2024-10-15 08:52:21', NULL, NULL, 'admin@mail.ru'),
(7, 'Гостевой дом \"Валентин\"', 'Атмосфера загородной жизни среди сосен с потрясающими морскими и горными пейзажами ﻿на берегу бухты с кристальной водой…\r\nВсего лишь 30 минут отделяют размеренный отдых в микрорайоне Голубая бухта ﻿от праздничной курортной жизни центра Геленджика!', 'Добро пожаловать в Гостевой дом \"Валентин\"!', 'https://valentin-gel.ru', 'https://valentin-gel.ru', 'г. Геленджик, Голубая бухта, ул. Ак. Ширшова, 14', '44.57939', '37.989735', 'hostel_images_card/EMrRGQkNmYczDkTcoe2yX8wrAkgeSBHX8Lt3b1Pt.jpg', 'hostel_images_card/kB2fZDTI1CdZfD0XyoGTQX5bzE6ATKz2QmqzpaZd.jpg', 79000001045, 26, 4, 1, 6, 7, '2023-06-09 16:51:46', '2023-06-09 16:51:46', NULL, NULL, 'admin@mail.ru'),
(8, 'Гостевой дом «Руслан»', 'Уютные большие номера в гостевом доме, индивидуальный санузел, кухня, бесплатная парковка, большой двор, мангальная зона, до моря 50 метров', 'Уютный гостевой дом', 'https://tvil.ru/city/gelendzhik/hotels/430342/', 'https://tvil.ru/city/gelendzhik/hotels/430342/', 'Россия, Геленджик, ул. Курзальная, 5а', '44.555502', '38.075129', 'hostel_images_card/GrdKhdWjpiQdcpk6avgEnZHUgO59syvr1ZqA1fyo.jpg', 'hostel_images_card/z7dSn7KUipdBQdR8QcdMcaVIzJ0HEiV8n1gK1IXc.jpg', 79180581212, 34, 4, 1, 6, 7, '2023-07-06 18:55:53', '2023-07-06 18:59:10', NULL, NULL, 'ruslan-gelendg82@mail.ru'),
(9, 'Гостиница \"История\"', 'Гостиница \"История\" – это современная гостиница со всеми удобствами и удобным расположением для туризма и отдыха.  Для нас важен каждый клиент, и мы прилагаем все усилия для обеспечения комфортных условий проживания.', '\"История\" – это современная гостиница в Дербенте', 'https://gostinica-istoriya.ru', 'https://gostinica-istoriya.ru', 'Россия, Дербент, пер. Карла Маркса, 66', '42.05143', '48.298011', 'hostel_images_card/0Fk4dibf1QniMAnHdPSQv0zIpDImZS9quLKhse2O.jpg', 'hostel_images_card/LMZ6gzNEu0g4LfVq7hiux2Tj5hrvG43mhZIrFpfN.jpg', 79382015858, 28, 4, 7, 10, 17, '2023-07-07 06:17:44', '2024-10-15 08:21:07', NULL, NULL, 'hotel_istoria@mail.ru'),
(10, 'Гостевой дом Вито', 'Наш гостевой дом расположен в удобном месте, всего в нескольких минутах ходьбы от пляжа и недалеко от множества ресторанов, кафе и магазинов.\r\nМы уверены, что вы будете чувствовать себя комфортно и уютно в Гостевом доме Вито.', 'Мы предлагаем прекрасный выбор экономичных номеров для комфортного отдыха', 'http://vito-home.ru', 'http://vito-home.ru', 'Россия, Геленджик, улица Розы Люксембург 26', '44.566195', '38.085873', 'hostel_images_card/PTicJ7XvGrk29LpFClmh8u9KV5oTB42no96lOpHP.jpg', 'hostel_images_card/IKWEDPj6ARDBOYUB1S2ARnpVBhMHlyO0B4NIwaaf.jpg', 79284505692, 24, 4, 1, 6, 7, '2023-07-09 04:54:56', '2023-07-09 04:54:56', NULL, NULL, 'Oksana.9338@mail.ru'),
(11, 'Гостевой дом \"Квен Очаг\"', 'В доме есть изделия старинной и современной ручной работы. По предварительной записи можно заказать мастер классы по приготовлению национальной еды или по изделиям натуральной кожи.\r\nРядом 2 магазина.', 'Отличаемся тем, что в доме все сделано моими руками', 'https://kven-ochag.clients.site', 'https://kven-ochag.clients.site', 'Россия, Республика Дагестан, село Заиб, Заибская улица, 46', '42.489169', '46.7097', 'hostel_images_card/cR9CingHUs7enphNt6mLUeWbrVqT8AVdNwORHD1k.jpg', 'hostel_images_card/YDrfF1s9ZKwm0Izc6tIzYQqYja1zqvSSC11R69Nu.jpg', 79884687817, 36, 4, 1, 5, 11, '2023-07-09 08:55:24', '2023-07-09 08:55:24', NULL, NULL, 'shamay.yusupova.82@mail.ru');

